section.pre:
declare variable $nachname as xs:string external;
<buecher autor="{$nachname}">
{
  let $bibliothek := .
  for $x in $bibliothek/buecher//buch,
      $y in $bibliothek/autoren//autor
  where starts-with($y, $nachname)
      and $x/@autorId = $y/@id
  order by $x/ausgabe
  return
  <buch ausgabe="{$x/ausgabe}">
    {$x/titel}
  </buch>
}
</buecher>
