let $gekuendigt := if (collection('/personal')/*[personal-id = $personal-id and fn:exists(kuendingung)] then 'ja' else 'nein' return common:render-response(concat('{"gekuendigt":"',$gekuendigt,'", "personal-id":"',$personal-id,'"}'))
