xquery version "1.0-ml";
declare namespace k = 'http://www.tekturcms.de/kunden'; declare namespace b = 'http://www.tekturcms.de/bestellungen';
import module namespace tde = "http://marklogic.com/xdmp/tde" at "/MarkLogic/tde.xqy";
declare function local:loadKunde($id, $name, $eMail) { let $root := <k:kunde> <k:id>{ $id }</k:id> <k:name>{ $name }</k:name> <k:eMail>{ $eMail }</k:eMail> </k:kunde>, $options := <options xmlns="xdmp:document-insert"> <permissions>{ xdmp:default-permissions() }</permissions> <collections> <collection>/kunden</collection> </collections> </options>, $fname := concat('/kunden/',$id,".xml") return xdmp:document-insert($fname,$root,$options) };
declare function local:loadBestellung($id, $datum, $preis, $kunde-id) { let $root := <b:bestellung> <b:id>{ $id }</b:id> <b:datum>{ $datum }</b:datum> <b:preis>{ $preis }</b:preis> <b:kunde-id>{ $kunde-id }</b:kunde-id> </b:bestellung>, $options := <options xmlns="xdmp:document-insert"> <permissions>{ xdmp:default-permissions() }</permissions> <collections> <collection>/bestellungen</collection> </collections> </options>, $fname := concat('/bestellungen/',$id,".xml") return xdmp:document-insert($fname,$root,$options) };
