declare function local:insertBestellungenSchema() { let $sql-view-name := 'bestellungen-view.xml', $sql-view := <template xmlns="http://marklogic.com/xdmp/tde"> <path-namespaces> <path-namespace> <prefix>b</prefix> <namespace-uri>http://www.tekturcms.de/bestellungen</namespace-uri> </path-namespace> </path-namespaces> <context>/b:bestellung</context> <collections> <collections-and> <collection>/bestellungen</collection> </collections-and> </collections> <rows> <row> <schema-name>bestellungen_schema</schema-name> <view-name>bestellungen_view</view-name> <columns> <column> <name>id</name> <scalar-type>string</scalar-type> <val>b:id</val> </column> <column> <name>datum</name> <scalar-type>string</scalar-type> <val>b:datum</val> </column> <column> <name>preis</name> <scalar-type>string</scalar-type> <val>b:preis</val> </column> <column> <name>kunde_id</name> <scalar-type>string</scalar-type> <val>b:kunde-id</val> </column> </columns> </row> </rows> </template> return tde:template-insert(concat('/templates/', $sql-view-name), $sql-view, xdmp:default-permissions()) }; local:loadKunde("1","Alex","tekturcms@gmail.com"), local:loadKunde("2","Horst","horst@horst.de"), local:loadKunde("3","Gundula","gundl@gundula.de"), local:loadBestellung("1","02.01.2019","99.90","2"), local:loadBestellung("2","03.01.2019","68.90","1"), local:insertKundenSchema(), local:insertBestellungenSchema(),
