body.pre:
element addressBook {
    element card {
        element name { text },
        element email { text }
    }*
}

section.pre:
unbehandeltesElement =
    element * - (aussteller | 
                 empfaenger |
                 datum) {
        (attribute * { text } | 
         text | 
         unbehandelteElemente)*
    }

start =
    element abrechung {
        element id { xsd:NMTOKEN },
        element datum  { xsd:date },
        [...]
        element zahlungen {
            element zahlung {
                element id { xsd:NMTOKEN },
                element datum  { xsd:date },
                element plan { xsd:NMTOKEN },
                [...]
            },
            element beleg-daten {
                element beleg {
                    attribute nummer { xsd:integer }?,
                    element datum  { xsd:date },
                    unbehandeltesElement+,
                    element aussteller { text },
                    unbehandeltesElement+,
                    element empfaenger { text },
                    unbehandeltesElement+
                }
            }
        }+
    }
}

namespace sch = "http://purl.oclc.org/dsdl/schematron"

start = ul 
ul =
  [ 
    sch:pattern [
      sch:rule [
      context = "list"
        sch:assert [
          test = "not(ancestor::ul and ancestor::procedure)"
                 'Eine ul darf in der Procedure nicht verschachtelt werden!'
          ]
        ]
      ]
    ]
  element ul {
    any_attribute*, 
    listitem+
  }

listitem =
  element listitem {
    any_attribute*, 
    ( para | ul )+
  }

para =
  element para {
    any_attribute*, 
    text*
  }
any_attribute = attribute * { text }
