section.pre:
xquery version "1.0-ml";

xdmp:set-response-content-type("text/html"),
let $pages :=
<html>
    <body>
        {
            for $chapter in collection("/chapter")/descendant::chapter
                return (
                    <h3>{ $chapter/title/text() }</h3>,
                    <p>{ $chapter/content/text() }</p> 
                )
        }
    </body>
</html>
return $pages

xquery version "1.0-ml";

